import { BlocksRenderer } from "@/registry/new-york/components/blocks-renderer";
import type { HelloWorldBlock } from "@/registry/new-york/lib/payload-types";

// Sample data - replace this with actual PayloadCMS data fetching
const sampleBlocks: HelloWorldBlock[] = [
  {
    blockType: "hello-world",
    title: "Welcome to PayloadCMS Blocks",
    content:
      "This is your HelloWorld block component working perfectly with the configuration.",
    buttonText: "Get Started",
    id: "1",
  },
  {
    blockType: "hello-world",
    title: "Another Block Instance",
    content:
      "You can have multiple instances of the same block with different content.",
    buttonText: "Learn More",
    id: "2",
  },
];

export default function HelloWorldDemoPage() {
  return (
    <main className="container mx-auto py-8 px-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold mb-8 text-center">
          PayloadCMS Blocks Demo
        </h1>
        <BlocksRenderer blocks={sampleBlocks} />
      </div>
    </main>
  );
}

import { Button } from "@/registry/new-york/ui/button";
import type { HelloWorldBlock as HelloWorldBlockType } from "@/registry/new-york/lib/payload-types";

export function HelloWorldBlock({
  title,
  content,
  buttonText,
}: Omit<HelloWorldBlockType, "blockType" | "id">) {
  return (
    <div className="p-6 border rounded-lg">
      <h2 className="text-xl font-semibold mb-4">{title}</h2>
      <p className="text-muted-foreground mb-4">{content}</p>
      <Button>{buttonText}</Button>
    </div>
  );
}

import type { Block } from 'payload'

export const HelloWorld: Block = {
  slug: 'hello-world',
  interfaceName: 'HelloWorldBlock',
  fields: [
    {
      name: 'title',
      type: 'text',
      required: true,
      defaultValue: 'Hello World Payload Block',
    },
    {
      name: 'content',
      type: 'text',
      required: false,
      defaultValue: 'This is a payload block component with both component.tsx and config.ts files.',
    },
    {
      name: 'buttonText',
      type: 'text',
      required: false,
      defaultValue: 'Click me',
    },
  ],
}
